import React from 'react';
import './ShowWeather.scss';


const ShowWeather = ({checkFetching, weather}) => {

  console.log('weather show', weather);
  console.log('checkFetching show', checkFetching);

  const calvinToCelsium = (tempInCal) => {
    const tempInCel = tempInCal - 273.15;
    return tempInCel.toFixed(2);
  }

  return (
    <div className="ShowWeather">
       {
        checkFetching
        ?<div className="result">
        {weather.city && weather.country && <div><p >Location: {weather.city}, {weather.country}</p></div>}
        {weather.temp && <div><p >Temperature: {calvinToCelsium(weather.temp)}</p></div>}
        {weather.humidity && <div><p >Humidity: {weather.humidity}</p></div>}
        {weather.description && <div><p >Description: {weather.description}</p></div>}
        {weather.error && <div><p >error: {weather.error}</p></div>}
        </div>
        :<p className="init-text">Please, enter the city above</p>
      } 
    </div>
  )
}
export default ShowWeather;
